import{_ as r,c as a,o as i,ad as t}from"./chunks/framework.DtW8lrcE.js";const h=JSON.parse('{"title":"Requirements Specification","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"en/guide/install-requirements.md","filePath":"en/guide/install-requirements.md","lastUpdated":1739529686000}'),o={name:"en/guide/install-requirements.md"};function n(s,e,l,u,c,d){return i(),a("div",null,e[0]||(e[0]=[t('<h1 id="requirements-specification" tabindex="-1">Requirements Specification <a class="header-anchor" href="#requirements-specification" aria-label="Permalink to &quot;Requirements Specification&quot;">​</a></h1><h2 id="pre-deployment-checks" tabindex="-1">Pre-deployment Checks <a class="header-anchor" href="#pre-deployment-checks" aria-label="Permalink to &quot;Pre-deployment Checks&quot;">​</a></h2><h2 id="hardware-requirements" tabindex="-1">Hardware Requirements <a class="header-anchor" href="#hardware-requirements" aria-label="Permalink to &quot;Hardware Requirements&quot;">​</a></h2><h3 id="operating-system-os" tabindex="-1">Operating System (OS) <a class="header-anchor" href="#operating-system-os" aria-label="Permalink to &quot;Operating System (OS)&quot;">​</a></h3><ul><li>It is recommended to use Linux or *nix operating systems (such as Ubuntu, Debian, etc.).</li><li>Non-Linux operating systems generally provide a poorer Docker experience and are strongly discouraged. Our support for installation or troubleshooting on non-Linux operating systems will be significantly reduced. If you still wish to try using a non-Linux operating system, you can set it up as follows: <ul><li>Windows: Use Docker Desktop on Windows or WSL 2.</li><li>macOS: Use Docker Desktop on Mac.</li></ul></li></ul><h3 id="memory-ram" tabindex="-1">Memory (RAM) <a class="header-anchor" href="#memory-ram" aria-label="Permalink to &quot;Memory (RAM)&quot;">​</a></h3><ul><li>Minimum Requirement: 4GB</li><li>Recommended Configuration: 6GB</li></ul><h3 id="processor-cpu" tabindex="-1">Processor (CPU) <a class="header-anchor" href="#processor-cpu" aria-label="Permalink to &quot;Processor (CPU)&quot;">​</a></h3><ul><li>Minimum Requirement: 2 cores</li><li>Recommended Configuration: 4 cores</li></ul><h3 id="storage" tabindex="-1">Storage <a class="header-anchor" href="#storage" aria-label="Permalink to &quot;Storage&quot;">​</a></h3><ul><li>It is recommended to use Unix-compatible file systems (such as EXT4, ZFS, APFS, etc.) that support user/group ownership and permissions.</li><li>Scraping data and other media-related data may increase the size of the media library by 10-20%.</li></ul><h2 id="software-requirements" tabindex="-1">Software Requirements <a class="header-anchor" href="#software-requirements" aria-label="Permalink to &quot;Software Requirements&quot;">​</a></h2><p>AMMDS requires Docker with Docker Compose plugin:</p><ul><li><strong>Docker Engine</strong>: This CLI variant is designed for Linux servers (or use via WSL2 on Windows).</li><li><strong>Docker Desktop</strong>: This GUI variant is not recommended for Linux but can be used on Windows or macOS.</li></ul><br><footer style="font-size:0.8em;"> Technical support provided by Xinjiang Mersen Software Development Studio <br> Copyright © 2025-2025 <a href="https://beian.miit.gov.cn" rel="noreferrer" target="_blank">ICP备案2023003452号-1</a></footer>',16)]))}const p=r(o,[["render",n]]);export{h as __pageData,p as default};
