import{_ as i,C as o,c as t,o as r,j as e,G as d,ad as c,a as s}from"./chunks/framework.DtW8lrcE.js";const f=JSON.parse('{"title":"Telegram","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"guide/usage/integration/Telegram.md","filePath":"guide/usage/integration/Telegram.md","lastUpdated":1744973947000}'),n={name:"guide/usage/integration/Telegram.md"};function h(u,l,m,p,g,b){const a=o("a-image");return r(),t("div",null,[l[0]||(l[0]=e("h1",{id:"telegram",tabindex:"-1"},[s("Telegram "),e("a",{class:"header-anchor",href:"#telegram","aria-label":'Permalink to "Telegram"'},"​")],-1)),l[1]||(l[1]=e("p",null,"Telegram 插件是一个消息通知插件，用于将系统的重要通知通过 Telegram Bot 发送给用户。它支持自定义消息模板、消息测试和 Webhook 回调等功能。",-1)),d(a,{style:{"border-radius":"12px"},src:"/images/usage/integration-telegram-001.png"}),l[2]||(l[2]=c('<h2 id="配置说明" tabindex="-1">配置说明 <a class="header-anchor" href="#配置说明" aria-label="Permalink to &quot;配置说明&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">提示</p><p>记得及时保存修改的配置哦！</p></div><h3 id="基本配置" tabindex="-1">基本配置 <a class="header-anchor" href="#基本配置" aria-label="Permalink to &quot;基本配置&quot;">​</a></h3><ul><li><strong>启用</strong>：是否启用 Telegram 通知插件。</li><li><strong>服务地址</strong>：Telegram API 的服务地址，默认为 <code>https://api.telegram.org</code>。如果无法访问，请注意配置网络代理。</li><li><strong>Bot Token</strong>：Telegram Bot 的访问令牌，可以从 <a href="https://t.me/BotFather" target="_blank" rel="noreferrer">@BotFather</a> 获取。</li><li><strong>Chat ID</strong>：接收消息的目标聊天 ID，可以是个人、群组或频道的 ID。</li><li><strong>Webhook URL</strong>：用于接收 Telegram 的消息回调，需要配置为可以公网访问的地址(不建议手动设置)。</li></ul><h4 id="获取-chat-id" tabindex="-1">获取 Chat ID <a class="header-anchor" href="#获取-chat-id" aria-label="Permalink to &quot;获取 Chat ID&quot;">​</a></h4><ol><li>打开 Telegram 应用。</li><li>搜索并添加 BotFather。</li><li>发送 <code>/newbot</code> 命令，按照提示创建一个新的 Bot。</li><li>完成 Bot 的创建后，BotFather 会提供一个 Bot Token。</li><li>打开 Telegram 应用，搜索并添加刚刚创建的 Bot。</li><li>发送 <code>/start</code> 命令，Bot 会向你发送一条消息。</li><li>在插件配置中填写 Bot Token 打开插件开关, 后保存配置。</li><li>在 Telegram 应用中，找到刚刚创建的 Bot, 发送一条消息, 任意文本即可。</li><li>回到插件配置中, 点击「获取 Chat ID」按钮, 即可获取 Chat ID, 请注意核对弹窗的消息文本是否与上一步发送的文本一致。</li><li>自动获取的 Chat ID 可能与上一步发送的不一致, 请手动获取。</li><li>保存配置, 即可开始使用。</li><li>点击「发送测试」按钮验证配置是否正确。</li></ol><h2 id="使用说明" tabindex="-1">使用说明 <a class="header-anchor" href="#使用说明" aria-label="Permalink to &quot;使用说明&quot;">​</a></h2><h3 id="配置验证" tabindex="-1">配置验证 <a class="header-anchor" href="#配置验证" aria-label="Permalink to &quot;配置验证&quot;">​</a></h3><ol><li>确保已正确填写 Bot Token 和 Chat ID。</li><li>点击「发送测试」按钮验证配置是否正确。</li><li>如果配置正确，将收到一条测试消息。</li></ol><h3 id="消息测试" tabindex="-1">消息测试 <a class="header-anchor" href="#消息测试" aria-label="Permalink to &quot;消息测试&quot;">​</a></h3><ul><li>在「消息测试」区域可以发送自定义消息进行测试。</li><li>支持发送文本、图片等多种类型的消息。</li><li>可以预览消息的实际显示效果。</li></ul><h3 id="命令说明" tabindex="-1">命令说明 <a class="header-anchor" href="#命令说明" aria-label="Permalink to &quot;命令说明&quot;">​</a></h3><h4 id="基础配置命令" tabindex="-1">基础配置命令 <a class="header-anchor" href="#基础配置命令" aria-label="Permalink to &quot;基础配置命令&quot;">​</a></h4><ul><li><code>/ammds</code> - 欢迎词语</li><li><code>/start</code> - 启用 AMMDS Bot，开始接收通知</li><li><code>/stop</code> - 停用 AMMDS Bot，暂停接收通知</li><li><code>/help</code> - 显示帮助信息，包含所有可用命令说明</li><li><code>/version</code> - 显示当前系统版本信息</li></ul><h4 id="搜索订阅命令" tabindex="-1">搜索订阅命令 <a class="header-anchor" href="#搜索订阅命令" aria-label="Permalink to &quot;搜索订阅命令&quot;">​</a></h4><ul><li><p><code>/search</code> - 搜索媒体资源</p><ul><li>使用方法：<code>/search 番号ID</code></li><li>示例：<code>/search ABP-123</code></li></ul></li><li><p><code>/subscribe</code> - 订阅媒体资源</p><ul><li>使用方法：<code>/subscribe 番号ID</code></li><li>当有新的匹配资源时会收到通知</li></ul></li><li><p><code>/unsubscribe</code> - 取消订阅</p><ul><li>使用方法：<code>/unsubscribe 番号ID</code></li><li>取消之前设置的订阅</li></ul></li></ul><h4 id="收藏管理命令" tabindex="-1">收藏管理命令 <a class="header-anchor" href="#收藏管理命令" aria-label="Permalink to &quot;收藏管理命令&quot;">​</a></h4><ul><li><p><code>/favorite</code> - 收藏媒体资源</p><ul><li>使用方法：<code>/favorite 番号ID</code></li><li>将感兴趣的资源加入收藏列表</li></ul></li><li><p><code>/unfavorite</code> - 取消收藏</p><ul><li>使用方法：<code>/unfavorite 番号ID</code></li><li>从收藏列表中移除资源</li></ul></li></ul><h4 id="系统任务命令" tabindex="-1">系统任务命令 <a class="header-anchor" href="#系统任务命令" aria-label="Permalink to &quot;系统任务命令&quot;">​</a></h4><ul><li><p><code>/scan</code> - 运行所有扫描任务</p><ul><li>扫描媒体库中的所有资源</li><li>更新媒体信息和元数据</li></ul></li><li><p><code>/scrape</code> - 运行一次刮削任务</p><ul><li>从网络获取媒体详细信息</li><li>更新封面、简介等元数据</li></ul></li><li><p><code>/media_sync</code> - 同步媒体库</p><ul><li>同步本地媒体文件与数据库</li><li>清理无效的媒体记录</li></ul></li><li><p><code>/restart</code> - 重启服务</p><ul><li>重启 AMMDS 系统服务</li><li>请谨慎使用此命令</li></ul></li></ul><h3 id="常见问题" tabindex="-1">常见问题 <a class="header-anchor" href="#常见问题" aria-label="Permalink to &quot;常见问题&quot;">​</a></h3><ol><li><p>如果无法收到消息，请检查：</p><ul><li>Bot Token 是否正确</li><li>Chat ID 是否正确</li><li>网络连接是否正常</li><li>代理设置是否正确</li></ul></li><li><p>如果需要在群组中使用：</p><ul><li>将机器人添加到群组</li><li>获取群组的 Chat ID</li><li>确保机器人有发送消息的权限</li><li>不推荐在群组中使用，避免消息过于频繁</li></ul></li></ol>',22))])}const k=i(n,[["render",h]]);export{f as __pageData,k as default};
